    public String getCedula()
    {
        String cedula="";
        if(rbCiudadano.isSelected()==true){
            cedula=cCiudadano.getText();
        }
        if(rbIndigena.isSelected()==true){
            cedula=cIndigena.getText();
        }
        if(rbInmigrante.isSelected()==true){
            cedula=cInmigrante.getText();
        }
        if(rbRefugiado.isSelected()==true){
            cedula=cRefugiado.getText();
        }
        return cedula;
    }
     public String getLocalizacion()
    {
        String localizacion="";
        if(rbCiudadano.isSelected()==true){
            localizacion=lCiudadano.getText();
        }
        if(rbIndigena.isSelected()==true){
            localizacion=lIndigena.getText();
        }
        if(rbInmigrante.isSelected()==true){
            localizacion=lInmigrante.getText();
        }
        if(rbRefugiado.isSelected()==true){
            localizacion=lRefugiado.getText();
        }
        return localizacion;
    }
    public void modificarEstado(){
    if(rbCiudadano.isSelected()==true){
        cCiudadano.setEditable(true);
    }else{
        cCiudadano.setEditable(false);
    }if(rbIndigena.isSelected()==true){
        cIndigena.setEditable(true);
        lIndigena.setEditable(true);
    }else{
        cIndigena.setEditable(false);
        lIndigena.setEditable(false);
    }if(rbInmigrante.isSelected()==true){
        cInmigrante.setEditable(true);
        lInmigrante.setEditable(true);
    }else{
        cInmigrante.setEditable(false);
        lInmigrante.setEditable(false);
    }if(rbRefugiado.isSelected()==true){
        cRefugiado.setEditable(true);
        lRefugiado.setEditable(true);
    }else{
        cRefugiado.setEditable(false);
        lRefugiado.setEditable(false);
    }
    }
